package aula3Fabri;

public class DesvioCondicionalComposto {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		int y = 20;

		if (x > y)
			System.out.println("X é maior do que Y!");
		else 
			System.out.println("Y é maior do que X!");

	}

}
